import ast
import os

def check_docstring_coverage(directory):
    total_functions = 0
    documented_functions = 0
    total_classes = 0
    documented_classes = 0

    for root, dirs, files in os.walk(directory):
        if '__pycache__' in root or '.venv' in root:
            continue

        for file in files:
            if file.endswith('.py'):
                try:
                    with open(os.path.join(root, file), 'r', encoding='utf-8') as f:
                        content = f.read()
                    tree = ast.parse(content)

                    for node in ast.walk(tree):
                        if isinstance(node, ast.FunctionDef):
                            total_functions += 1
                            if ast.get_docstring(node):
                                documented_functions += 1
                        elif isinstance(node, ast.ClassDef):
                            total_classes += 1
                            if ast.get_docstring(node):
                                documented_classes += 1
                except:
                    continue

    print(f'Functions: {documented_functions}/{total_functions} ({documented_functions/total_functions*100:.1f}% documented)')
    print(f'Classes: {documented_classes}/{total_classes} ({documented_classes/total_classes*100:.1f}% documented)')

check_docstring_coverage('src/pynomaly')
")
